using namespace std;
int lengthOfLongestSubstring1(string s) {
        if (s.empty()) return 0;
        int size = s.size();
        int pos[128];
        memset(pos, -1, 128 * sizeof(int));
        int max = 0;
        int offset = 0;     // offset marks the new start of the substring
        for (int i=0; i < size; i++) {
            char ch = s[i];
            if (pos[ch] < offset)   // s[i] doesn't exist in substring,
            {
                pos[ch] = i; // mark s[i] with its position
                max = i+1-offset>max? i + 1 -offset:max;
            }
            else // s[i] exists in substring
            {
                offset = pos[ch] + 1;
                pos[ch] = i;
            }
        }
        return max;
    }
long long lengthOfLongestSubstring(string s){ 
	if(s.size() == 0 || s.size() == 1)
		return s.size();
	vector<long long> charPos(256, 256);
	long long currLen=0L, maxLen=0L;
	for(long long i=0;i<s.size();++i){
		char c = s[i];
		//printf("Char:%c - %d, Position:%d\n", c, c, charPos[c]);
		if(c >= 256)
			cout << "Found " << c << endl;
		if(charPos[c] == 256){
			currLen += 1;
		}else{
			if(charPos[c] < i-currLen){
				currLen += 1;
			}else{
				if(currLen > maxLen){
					maxLen = currLen;
				}
				//printf("c:%c charPos[c]:%d,i:%d, currLen:%d\n",c ,charPos[c], i, currLen);
				currLen = i-charPos[c];
				//printf("c:%c charPos[c]:%d,i:%d, currLen:%d\n",c ,charPos[c], i, currLen);
			}
		}
		charPos[c] = (long long)i;
	}
	maxLen = (maxLen < currLen) ? currLen : maxLen;
	return maxLen;
}
